import React, {Component} from 'react';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
          angka1:0,
          angka2:0,
          hasil:0,
          operator:"",
          isSubmitted:false
        };
        this.makeButton = this.makeButton.bind(this);
        this.makeOperator = this.makeOperator.bind(this);
    }

    update = (event) => {
        this.setState({ [event.target.button]: event.target.value });
        alert(this.state.number);
    }

    makeButton(min,max) {
      let buttonArray = [];

      for (let index = min; index <= max; index++) {
        let cl = "button-" + index
        buttonArray.push(<button name={cl} onClick={this.update}>{index}</button>);
      }
      return buttonArray;
    }

    makeOperator(type) {
      let buttonArray = {
        add: "+",
        substract: "-",
        divide: "-",
        multiply: "-",
        submit: "-"
      };

      return buttonArray[type];
    }

    result = () => {
        let operator = this.state.operator;
        let angka1 = parseInt(this.state.angka1);
        let angka2 = parseInt(this.state.angka2);
        let hasil = 0;

        switch (operator) {
            case "+":
                hasil = angka1 + angka2;
                break;
            case "-":
                hasil = angka1 - angka2;
                break;
            case "*":
                hasil = angka1 * angka2;
                break;
            default:
                hasil = angka1 / angka2;
                break;
        }

        this.setState({
            hasil,
            isSubmitted: true
        });
        alert(hasil);
    };

    render() {
        return (
            <div>
                <center>
                    <div>
                      {this.makeButton(1,3)}
                      {this.makeOperator("add")}
                    <div>
                      {this.makeButton(4,6)}
                    </div>
                      {this.makeButton(7,9)}
                    <div>
                      {this.makeButton(0,0)}
                    </div>
                        <button onClick={this.result}>Submit</button>
                    </div>
                </center>
            </div>
        );
    }
}

export default App;
