import React, { Component } from "react";
import "./App.css";
class App extends Component {

  render() {
addToInput = () => {};
addZeroToInput = () => {};
multiply = () => {};
divide = () => {};
add = () => {};
subtract = () => {};
evaluate = () => {};
clearInput = () => {};

    return (
      <div className="App">
        <div className="calc-wrapper">
          <div className="row">
            <div>Input will be here</div>
          </div>
          <div className="row">
            <button handleClick={this.addToInput}>7</button>
            <button handleClick={this.addToInput}>8</button>
            <button handleClick={this.addToInput}>9</button>
            <button handleClick={this.divide}>/</button>
          </div>
          <div className="row">
            <button handleClick={this.addToInput}>4</button>
            <button handleClick={this.addToInput}>5</button>
            <button handleClick={this.addToInput}>6</button>
            <button handleClick={this.multiply}>*</button>
          </div>
          <div className="row">
            <button handleClick={this.addToInput}>1</button>
            <button handleClick={this.addToInput}>2</button>
            <button handleClick={this.addToInput}>3</button>
            <button handleClick={this.add}>+</button>
          </div>
          <div className="row">
            <button style={{flexGrow: 2}} handleClick={this.addZeroToInput}>0</button>
            <button style={{flexGrow: 1}} handleClick={this.subtract}>-</button>
            <button style={{flexGrow: 1}} handleClick={this.evaluate}>=</button>
          </div>
          <div className="row">
            <button style={{flexGrow: 1}} handleClear={this.clearInput}>Clear</button>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
