import React, {Component} from 'react';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
          result:0,
        };
        this.makeButton = this.makeButton.bind(this);
        this.makeOperator = this.makeOperator.bind(this);
    }

    update = (event) => {
        this.setState({[this.state.result]: event.target.name});
        console.log(event.target.name);
    }

    makeButton(min,max) {
      let buttonArray = [];

      for (let index = min; index <= max; index++) {
        let cl = "button-" + index
        buttonArray.push(<button key={cl} className="numberButton" name={index} onClick={this.update}>{index}</button>);
      }
      return buttonArray;
    }

    makeOperator(handleClick, operationType) {
      let operationTypeList = {
        add: "+",
        subtract: "-",
        divide: "/",
        multiply: "*",
        submit: "="
      };

      let cl = "button-" + operationType
      return (<button key={cl} className="operationButton" name={operationTypeList[operationType]} onClick={handleClick}>{operationTypeList[operationType]}</button>);
    }

    render() {
        return (
            <div>
                <center>
                  {this.state.result}
                    <div>
                      {this.makeButton(1,3)}
                      {this.makeOperator(this.update, "add")}
                      {this.makeOperator(this.update, "subtract")}
                    <div>
                      {this.makeButton(4,6)}
                      {this.makeOperator(this.update, "divide")}
                      {this.makeOperator(this.update, "multiply")}
                    </div>
                      {this.makeButton(7,9)}
                      {this.makeButton(0,0)}
                      {this.makeOperator(this.update, "submit")}
                    </div>
                </center>
            </div>
        );
    }
}

export default App;
