import React, { Component } from "react";
import "./App.css";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            result: 0,
            result2: 0,
            operatorType: ""
        };
        this.makeButton = this.makeButton.bind(this);
        this.makeOperator = this.makeOperator.bind(this);
    }

    updateResult = event => {
        this.setState({ result: event.target.name });
    };

    clearResult = event => {
        this.setState({ result: 0})
    }

    operateAdd = (event) => {
        let operatorType = this.state.operatorType

        this.setState({operatorType: event.target.name})
        console.log(operatorType)
    }


    makeButton(min, max) {
        let buttonArray = [];

        for (let index = min; index <= max; index++) {
            let cl = "button-" + index;
            buttonArray.push(
                <button
                    key={cl}
                    className="numberButton"
                    name={index}
                    onClick={this.updateResult}
                >
                    {index}
                </button>
            );
        }
        return buttonArray;
    }

    makeOperator(handleClick, operationType) {
        let operationTypeList = {
            add: "+",
            subtract: "-",
            divide: "/",
            multiply: "*",
            submit: "="
        };

        let cl = "button-" + operationType;
        return (
            <button
                key={cl}
                className="operationButton"
                name={operationTypeList[operationType]}
                onClick={handleClick}
            >
                {operationTypeList[operationType]}
            </button>
        );
    }

    render() {
        return (
            <div>
                <div className="wrapper">
                    <div className="result">{this.state.result}</div>
                    <div className="buttonWrapper">
                        <div className="row">
                            {this.makeButton(1, 3)}
                            {this.makeOperator(this.operateAdd, "add")}
                            {this.makeOperator(this.operateAdd, "subtract")}
                        </div>
                        <div className="row">
                            {this.makeButton(4, 6)}
                            {this.makeOperator(this.operateAdd, "divide")}
                            {this.makeOperator(this.operateAdd, "multiply")}
                        </div>
                        <div className="row">
                            {this.makeButton(7, 9)}
                            {this.makeButton(0, 0)}
                            {this.makeOperator(this.operateAdd, "submit")}
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
