import React, { Component } from "react";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            input: "",
            previousNumber: "",
            currentNumber: "",
            operator: ""
        };
    }

    addToInput = val => {
        this.setState({ input: this.state.input + val });
    };

    addDecimal = val => {
        // only add decimal if there is no current decimal point present in the input area
        if (this.state.input.indexOf(".") === -1) {
            this.setState({ input: this.state.input + val });
        }
    };

    addZeroToInput = val => {
        // if this.state.input is not empty then add zero
        if (this.state.input !== "") {
            this.setState({ input: this.state.input + val });
        }
    };

    clearInput = () => {
        this.setState({ input: "" });
    };

    add = () => {
        this.state.previousNumber = this.state.input;
        this.setState({ input: "" });
        this.state.operator = "plus";
    };

    subtract = () => {
        this.state.previousNumber = this.state.input;
        this.setState({ input: "" });
        this.state.operator = "subtract";
    };

    multiply = () => {
        this.state.previousNumber = this.state.input;
        this.setState({ input: "" });
        this.state.operator = "multiply";
    };

    divide = () => {
        this.state.previousNumber = this.state.input;
        this.setState({ input: "" });
        this.state.operator = "divide";
    };

    evaluate = () => {
        this.state.currentNumber = this.state.input;

        if (this.state.operator == "plus") {
            this.setState({
                input:
                    parseInt(this.state.previousNumber) +
                    parseInt(this.state.currentNumber)
            });
        } else if (this.state.operator == "subtract") {
            this.setState({
                input:
                    parseInt(this.state.previousNumber) -
                    parseInt(this.state.currentNumber)
            });
        } else if (this.state.operator == "multiply") {
            this.setState({
                input:
                    parseInt(this.state.previousNumber) *
                    parseInt(this.state.currentNumber)
            });
        } else if (this.state.operator == "divide") {
            this.setState({
                input:
                    parseInt(this.state.previousNumber) /
                    parseInt(this.state.currentNumber)
            });
        }
    };

    makeButton(min, max) {
        let buttonArray = [];

        for (let index = min; index <= max; index++) {
            let cl = "button-" + index;
            buttonArray.push(
                <div
                    className={cl}
                    onClick={() => this.addToInput(this.children)}
                >
                    {index}
                </div>
            );
        }
        return buttonArray;
    }

    makeOperator(handleClick, operationType) {
        let operationTypeList = {
            add: "+",
            subtract: "-",
            divide: "/",
            multiply: "*",
            submit: "="
        };

        return (
            <div
                className={operationType}
                buttonPressed={this.buttonPressed}
                onClick={handleClick}
            >
                {operationTypeList[operationType]}
            </div>
        );
    }

    render() {
        return (
            <div>
                <center>
                    <p>{this.state.result}</p>
                    <div>
                        {this.makeButton(1, 3)}
                        {this.makeOperator(this.update, "add")}
                        {this.makeOperator(this.update, "subtract")}
                        <div>
                            {this.makeButton(4, 6)}
                            {this.makeOperator(this.update, "divide")}
                            {this.makeOperator(this.update, "multiply")}
                        </div>
                        {this.makeButton(7, 9)}
                        {this.makeButton(0, 0)}
                        {this.makeOperator(this.update, "submit")}
                    </div>
                </center>
            </div>
        );
    }
}

export default App;
