import React, { Component } from "react";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            angka1: 0,
            angka2: 0,
            hasil: 0,
            result: "HI",
            operator: "",
            isSubmitted: false
        };
        this.makeButton = this.makeButton.bind(this);
        this.makeOperator = this.makeOperator.bind(this);
    }

    update = event => {
        this.setState({
            [this.buttonPressed]: event.currentTarget.nanme
        });
    };

    makeButton(min, max) {
        let buttonArray = [];

        for (let index = min; index <= max; index++) {
            let cl = "button-" + index;
            buttonArray.push(
                <button
                    name={cl}
                    buttonPressed={this.buttonPressed}
                    onClick={this.update}
                >
                    {index}
                </button>
            );
        }
        return buttonArray;
    }

    buttonPressed = buttonName => {
        this.setState({
            result: buttonName
        });
    };

    makeOperator(handleClick, operationType) {
        let operationTypeList = {
            add: "+",
            subtract: "-",
            divide: "/",
            multiply: "*",
            submit: "="
        };
        let cl = "button-" + operationType;

        return (
            <button
                name={cl}
                buttonPressed={this.buttonPressed}
                onClick={handleClick}
            >
                {operationTypeList[operationType]}
            </button>
        );
    }

    result = () => {
        let operator = this.state.operator;
        let angka1 = parseInt(this.state.angka1);
        let angka2 = parseInt(this.state.angka2);
        let hasil = 0;

        switch (operator) {
            case "+":
                hasil = angka1 + angka2;
                break;
            case "-":
                hasil = angka1 - angka2;
                break;
            case "*":
                hasil = angka1 * angka2;
                break;
            default:
                hasil = angka1 / angka2;
                break;
        }

        this.setState({
            hasil,
            isSubmitted: true
        });
        alert(hasil);
    };

    render() {
        return (
            <div>
                <center>
                    <p>{this.state.result}</p>
                    <div>
                        {this.makeOperator(this.update, "add")}
                        {this.makeOperator(this.update, "subtract")}
                        <div>
                            {this.makeButton(4, 6)}
                            {this.makeOperator(this.update, "divide")}
                            {this.makeOperator(this.update, "multiply")}
                        </div>
                        {this.makeButton(7, 9)}
                        {this.makeButton(0, 0)}
                        {this.makeOperator(this.update, "submit")}
                    </div>
                </center>
            </div>
        );
    }
}

export default App;
